import { useLocation } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import Tollbar from "../00ToolbarU/Toolbar";
import { Container, Typography, Button, Box,  } from "@mui/material";
import SearchIcon from "@mui/icons-material/Search";
import { Link } from "react-router-dom";
import { ArrowBackIosNew } from "@mui/icons-material";

const SearchResults = () => {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const searchTerm = queryParams.get("term");
  const searchMedia = queryParams.get("media");
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    axios
      .get(`http://localhost:3001/contents/search?term=${searchTerm}&media=${searchMedia}`)
      .then((response) => {
        setSearchResults(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [searchTerm, searchMedia]);

  return (
    <>
      <Tollbar />
      <Container>
        <Box
          display="flex"
          alignItems="center"
          sx={{
            mt: 5,
          }}
        >
          <SearchIcon
          sx={{
            color:"#6C757D",
            mr: 2,
          }}
          />
          <Typography
            component="h1"
            variant="h4"
            fontWeight={500}
            sx={{
              color: "#6C757D"
            }}
          >
            Resultados da Pesquisa: "{searchTerm}"
          </Typography>
          
        </Box>


        <Box>
          <Link to="/">
            <Button
              variant="secondary"
              size="medium"
              startIcon={<ArrowBackIosNew />}
              sx={{
                mr: 70,
                mt: 5,
                color: "#131313"
              }}
            />
          </Link>
        </Box>
      </Container>

      {searchResults.length > 0 ? (
        <>
          {searchResults.map((result) => (
            <li key={result._id}>{result.titulo}</li>
          ))}
        </>
      ) : (
        <p>Nenhum resultado encontrado.</p>
      )}
    </>
  );
};

export default SearchResults;
