import { useLocation } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import Tollbar from "../00ToolbarU/Toolbar";
import { Container, Typography, Button, Box, TextField, InputAdornment, FormControl, InputLabel, Select, MenuItem, Card, CardMedia, CardContent, Chip, CardActions, Stack } from "@mui/material";
import SearchIcon from "@mui/icons-material/Search";
import { Link } from "react-router-dom";
import { ArrowBackIosNew } from "@mui/icons-material";
import folder from "./folder.png"


const SearchResults = () => {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const searchTerm = queryParams.get("term");
  const searchMedia = queryParams.get("media");
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    axios
      .get(`http://localhost:3001/contents/search?term=${searchTerm}&media=${searchMedia}`)
      .then((response) => {
        setSearchResults(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [searchTerm, searchMedia]);



  const [search, setSearch] = useState("");

  const handleChange = (event) => {
    setSearch(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const searchTerm = search.trim();
    if (searchTerm) {
      const url = `/results?term=${searchTerm}`;
      window.location.href = url;
    }
  };

  const [tag, setTag] = useState('');
  const tagChange = (event) => {
    setTag(event.target.value);
  };


  const chipMidia = result.midia
  const chaves = [];
  // Loop sobre as chaves do objeto
  for (const chave in chipMidia) {
    // Verifica se o valor da chave é verdadeiro
    if (chipMidia[chave] === true) {
      // Adiciona o nome da chave ao array
      chaves.push(chave);
    }
  }
  // Converte o array em uma string separada por vírgulas
  const chavesString = chaves.join(', ');

  // Imprime a string com os nomes das chaves
  console.log(chavesString);

  return (
    <>
      <Tollbar />
      <Container>
        <Box
          display="flex"
          alignItems="center"
          sx={{
            mt: 5,
            display: "flex",
            justifyContent: "flex-start",
          }}
        >
          <Link to="/">
            <Button
              variant="secondary"
              size="medium"
              startIcon={<ArrowBackIosNew />}
              sx={{
                color: "#131313",
                height: 55,
              }}
            />
          </Link>

          <TextField
            id="search"
            type="search"
            label="Pesquisar Conteúdo"
            value={search}
            onChange={handleChange}
            sx={{
              width: "100%"
            }}
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <Link to="/Pesquisa">
                    <Button
                      sx={{ height: 55 }}
                      onClick={handleSubmit}
                    >
                      <SearchIcon />
                    </Button>
                  </Link>
                </InputAdornment>
              ),
            }}
          />

          <FormControl vairant="primary">
            <InputLabel id="demo-simple-select-filled-label">Tags</InputLabel>
            <Select
              labelId="demo-simple-select-filled-label"
              id="demo-simple-select-filled"
              value={tag}
              onChange={tagChange}
              sx={{
                width: 100,
                ml: 1,
              }}
            >
              <MenuItem
                value=""
              >
              </MenuItem>
              <MenuItem value={"Carreira"}>Carreira</MenuItem>
              <MenuItem value={"FundamentosDeUX"}>Fundamentos de UX</MenuItem>
              <MenuItem value={"DesignDeInteração"}>Design de Interação</MenuItem>
              <MenuItem value={"UIDesign"}>UI Design</MenuItem>
              <MenuItem value={"ArquiteturaDaInformacao"}>Arquitetura da Informação</MenuItem>
            </Select>
          </FormControl>
        </Box>

        <Box
          display="flex"
          alignItems="center"
          sx={{
            mt: 5,
            ml: 8,
            display: "flex",
            justifyContent: "flex-start",
          }}
        >
          <SearchIcon
            sx={{
              color: "#6C757D",
              mr: 1,
              fontSize: 35,
            }}
          />
          <Typography
            component="h1"
            variant="h4"
            fontWeight={500}
            sx={{
              color: "#6C757D"
            }}
          >
            Resultado da busca: "{searchTerm}"
          </Typography>
        </Box>
      </Container>

      <Container
        sx={{
          display: "flex",
          flexWrap: "wrap",
          mb: 10,
          alignItens: "center",
          justifyContent: "center",
        }}
      >
        {searchResults.length > 0 ? (
          <>
            {searchResults.map((result) => (
              <Box
                sx={{
                  display: "flex",
                  my: 5,
                  ml: 8,
                }}
              >
                <Card sx={{ mr: 2 }}>
                  <CardMedia
                    sx={{ height: 120 }}
                    image={folder}
                    tittle="Conteúdo"
                  />
                  <CardContent
                    key={result._id}
                    sx={{
                      width: 240,
                    }}
                  >
                    <Stack
                      direction="row"
                      spacing={2}
                      sx={{
                        alignItems: "center"
                      }}
                    >
                      <Typography variant="h6" component="div"> {result.titulo} </Typography>
                      <Chip variant="outlined" size="small" sx={{ mr: 1 }} label={result.titulo} />
                    </Stack>
                    <Typography variant="p" component="div"> {result.descricao} </Typography>
                  </CardContent>
                  <CardActions>
                    <Button
                      variant="outlined"
                      size="small"
                      sx={{
                        mt: -1,
                        ml: 1,
                        mb: 1,
                        color: "#0C2D8A",
                        borderColor: "#0C2D8A",
                        '&:hover': {
                          backgroundColor: "#0C2D8A",
                          color: "#BECBEA",
                        },
                      }}
                    >
                      Visualizar
                    </Button>
                  </CardActions>
                </Card>
              </Box>
            ))}
          </>
        ) : (
          <p>Nenhum resultado encontrado.</p>
        )}
      </Container>

      {/* {searchResults.length > 0 ? (
        <>
          {searchResults.map((result) => (
            <li key={result._id}>{result.titulo}</li>
          ))}
        </>
      ) : (
        <p>Nenhum resultado encontrado.</p>
      )} */}
    </>
  );
};

export default SearchResults;
